import React, { useEffect, useState } from 'react'
import { Outlet, useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify';
import moment from 'moment';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { fetchInternAttendanceRecords } from '../redux/slices/FetchDetailsSlice';

const SystemLayout = () => {
  const navigate = useNavigate();
  const dispach = useDispatch();
  useEffect(() => {
    if (!localStorage.getItem("token")) {
      navigate('/');
      toast.error("please login to enter");
    }
  }, [])
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/")
  }
  const [isPunchedIn, setIsPunchedIn] = useState(false);
  const [status, setStatus] = useState("In");
  const [date, setDate] = useState(new Date());
  useEffect(() => {
    const timer = setInterval(() => {
      setDate(new Date());
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, []);


  const handleSubmit = async (date, time) => {
    const attendance = {
      date: date,
      in_time: time,
      out_time: time,
    }
    try {
      const response = await axios.post('http://localhost:8000/api/v1/attendance', attendance, {
        headers: {
          Accept: 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        }
      })
      console.log(response);
      if (response.data.success) {
        toast.success(response.data.message);
      }
      else {
        toast.success(response.data.message);
      }
    } catch (error) {
      toast.error(error.response.data.message);
    }
  }

  useEffect(() => {
    dispach(fetchInternAttendanceRecords());
  }, [handleSubmit])

  const handlePunch = (e) => {
    e.preventDefault();
    const currentDate = moment().format('YYYY-MM-DD');
    const currentTime = moment().format('HH:mm:ss');
    // Handle Punch In or Punch Out logic here
    if (isPunchedIn) {
      // Handle Punch Out
      console.log('Punched Out at:', currentDate, currentTime);
    } else {
      // Handle Punch In
      console.log('Punched In at:', currentDate, currentTime);
    }
    setIsPunchedIn(!isPunchedIn); // Toggle punch status
  };


  return (
    <>
      <div className='flex justify-between items-center w-full py-10 px-10'>
        <div className='text-3xl italic font-serif'>Attendance Page</div>
        <div className='text-lg italic font-mono'>Welcome user</div>
        <button onClick={handleLogout} className='w-[5%] bg-red-500 text-white rounded-2xl active:scale-75 active:transition-all active:ease-in'>Logout</button>
      </div>
      <div className='flex justify-center'>
        <p className='pb-5 text-blue-500'> Date : {moment().format('LLLL')}</p>

        {/* <select className="h-10 rounded-lg" name="status" value={status} onChange={e => setStatus(e.target.value)}>
            <option>In</option>
            <option>Out</option>
          </select> */}
        <button onClick={(e) => { e.preventDefault(); handleSubmit(moment().format('YYYY-MM-DD'), moment().format('HH:mm:ss')) }} className='w-20 bg-green-500 rounded-md text-white ml-10 h-10'>Punch In</button>
        <button onClick={(e) => { e.preventDefault(); handleSubmit(moment().format('YYYY-MM-DD'), moment().format('HH:mm:ss')) }} className='w-20 bg-red-500 rounded-md text-white ml-10 h-10'>Punch Out</button>

      </div>

      <Outlet />
    </>
  )
}

export default SystemLayout
