// const hh = "2024-03-27 17:35:40";
// look: moment(hh).format('LLLL'),
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { fetchInternAttendanceRecords } from '../../redux/slices/FetchDetailsSlice';
const Attendance = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchInternAttendanceRecords());
  }, []);
  const attendanceRecords = useSelector(state => state.data.AttendanceRecords);
  return (
    <>

      <div className="flex justify-center">
        <div className="flex flex-col w-[80%] mt-10">
          <div className="-m-1.5 overflow-x-auto">
            <div className="p-1.5 min-w-full inline-block align-middle">
              <div className="border rounded-lg shadow overflow-hidden dark:border-gray-700 dark:shadow-gray-900">
                <table className="min-w-full divide-y divide-gray-800 dark:divide-gray-700 text-center">
                  <thead className="bg-gray-50 dark:bg-gray-700">
                    <tr>
                      <th scope="col" className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase dark:text-gray-400">Date</th>
                      <th scope="col" className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase dark:text-gray-400">In</th>
                      <th scope="col" className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase dark:text-gray-400">Out</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-800 dark:divide-gray-700">

                    {attendanceRecords?.length === 0 ? (<div className='text-center w-f'>no record found</div>) : attendanceRecords.map((intern, index) => {
                      return (
                        <tr key = {index}>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-800 dark:text-gray-800">{intern.date}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-800 dark:text-white bg-green-600">{intern.in_time || ''}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-800 dark:text-white bg-red-600 ">{intern.out_time || ''}</td>
                        </tr>
                      )
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default Attendance
