import React, {useEffect} from 'react'
import { Outlet, useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify';

const SystemLayout = () => {
  const navigate = useNavigate();
  useEffect(() => {
    if (!localStorage.getItem("token")) {
      navigate('/');
      toast.error("plea")
    }
  }, [])
  return (
    <Outlet/>
  )
}

export default SystemLayout
