import React, { useEffect, useState } from 'react'
import { Outlet, useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify';
import moment from 'moment';
import axios from 'axios';
import { useDispatch } from 'react-redux';

const SystemLayout = () => {
  const navigate = useNavigate();
  useEffect(() => {
    if (!localStorage.getItem("token")) {
      navigate('/');
      toast.error("please login to enter");
    }
  }, [])
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/")
  }

  const [status, setStatus] = useState("In");
  const [date, setDate] = useState(new Date());
  useEffect(() => {
    const timer = setInterval(() => {
      setDate(new Date());
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const attendance = {
      status: status,
      date_and_time: moment().format('YYYY-MM-DD HH:mm:ss'),
    }
    // const hh = "2024-03-27 17:35:40";
    // const trial = {
    //   moment : moment(hh).format('LLLL'),
    //   normal:  moment(hh).format('YYYY-MM-DD HH:mm:ss')
    // }
    // console.log("hello",trial);
    // console.log("hello",trial);
    try {
      const response = await axios.post('http://localhost:8000/api/v1/attendance', attendance, {
        headers: {
          Accept: 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        }
      })
      console.log(response);
      if (response.data.success) {
        toast.success(response.data.message);
      }
      else {
        toast.success(response.data.message);
      }
    } catch (error) {
      toast.error(error.response.data.message);
    }
  }

  const dispach = useDispatch();
  useEffect(() => {

  }, [handleSubmit])
  return (
    <>
      <div className='flex justify-between items-center w-full py-10 px-10'>
        <div className='text-3xl italic font-serif'>Attendance Page</div>
        <div className='text-lg italic font-mono'>Welcome user</div>
        <button onClick={handleLogout} className='w-[5%] bg-red-500 text-white rounded-2xl active:scale-75 active:transition-all active:ease-in'>Logout</button>
      </div>
      <div className='flex justify-center'>
        <form onSubmit={handleSubmit}>
          <p className='pb-5 text-blue-500'> Date : {moment().format('LLLL')}</p>
          <select className="h-10 rounded-lg" name="status" value={status} onChange={e => setStatus(e.target.value)}>
            <option>In</option>
            <option>Out</option>
          </select>
          <button type='submit' className='w-16 bg-green-500 rounded-md text-white ml-10 h-10'>Submit</button>
        </form>
      </div>

      <Outlet />
    </>
  )
}

export default SystemLayout
