import React, { useEffect, useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import moment from 'moment';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { fetchInternAttendanceRecords } from '../redux/slices/FetchDetailsSlice';

const SystemLayout = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  useEffect(() => {
    if (!sessionStorage.getItem('token')) {
      navigate('/');
      toast.error('Please login to enter');
    }
  }, []);

  const handleLogout = async (event) => {
    event.preventDefault();
    const response = await axios.post("http://localhost:8000/api/v1/logout", {}, {
      headers: {
        Accept: "application/json",
        Authorization: `Bearer ${sessionStorage.getItem("token")}`,
      }
    })
    if (response.data.su === "success") {
      toast.success(response.data.message)
      sessionStorage.removeItem("token");
      navigate("/");
    }
  }

  const [date, setDate] = useState(new Date());
  useEffect(() => {
    const timer = setInterval(() => {
      setDate(new Date());
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, []);

  const handleSubmit = async () => {
    const currentDate = moment().format('YYYY-MM-DD');
    const currentTime = moment().format('HH:mm:ss');
    const attendance = {
      date: currentDate,
      in_time: currentTime,
      out_time: currentTime,
    };
    try {
      const response = await axios.post(
        'http://localhost:8000/api/v1/attendance',
        attendance,
        {
          headers: {
            Accept: 'application/json',
            Authorization: `Bearer ${sessionStorage.getItem('token')}`,
          },
        }
      );
      if (response.data.success) {
        dispatch(fetchInternAttendanceRecords());
        toast.success(response.data.message);
      } else {
        toast.error(response.data.message);
      }
    } catch (error) {
      toast.error(error.response.data.message);
    }
  };

  const attendanceRecords = useSelector(state => state.data.AttendanceRecords);

  return (
    <>
      <div className="flex justify-between items-center w-full py-10 px-10">
        <div className="text-3xl italic font-serif">Attendance Page</div>
        {attendanceRecords && attendanceRecords.map((data) => <div className="text-lg italic font-mono">Welcome {data.intern.name}</div>
        )}
        <button
          onClick={handleLogout}
          className="w-[5%] bg-red-500 text-white rounded-2xl active:scale-75 active:transition-all active:ease-in"
        >
          Logout
        </button>
      </div>
      <div className="flex justify-center">
        <p className="pb-5 text-blue-500"> Date : {moment().format('LLLL')}</p>
        <button onClick={handleSubmit} className={`w-20 rounded-md text-white ml-10 h-10 bg-green-500`} >Punch In</button>
        <button onClick={handleSubmit} className={`w-20 rounded-md text-white ml-10 h-10 bg-red-500 `} >Punch Out</button>
      </div>
      <Outlet />
    </>
  );
};

export default SystemLayout;
