import React, { useEffect, useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import moment from 'moment';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { fetchInternAttendanceRecords } from '../redux/slices/FetchDetailsSlice';

const SystemLayout = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  useEffect(() => {
    if (!localStorage.getItem('token')) {
      navigate('/');
      toast.error('Please login to enter');
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const [isPunchedIn, setIsPunchedIn] = useState(false);
  const [isPunchedOut, setIsPunchedOut] = useState(true);
const [date, setDate] = useState(new Date());
  useEffect(() => {
    const timer = setInterval(() => {
      setDate(new Date());
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, []);

  const handleSubmit = async () => {
    if (type === 'punchIn') {
      setIsPunchedIn(true);
      setDisablePunchIn(true);
      setTimeout(() => {
        setIsPunchedIn(false);
        setDisablePunchIn(false);
      }, 120000); // Disable for 2 minutes (120000 milliseconds)
    } else if (type === 'punchOut') {
      setIsPunchedOut(true);
      setDisablePunchOut(true);
      setTimeout(() => {
        setIsPunchedOut(false);
        setDisablePunchOut(false);
      }, 120000); // Disable for 2 minutes (120000 milliseconds)
    }
    const currentDate = moment().format('YYYY-MM-DD');
    const currentTime = moment().format('HH:mm:ss');
    setIsPunchedIn(!isPunchedIn);
    const attendance = {
      date: currentDate,
      in_time: currentTime,
      out_time: currentTime,
    };
    try {
      const response = await axios.post(
        'http://localhost:8000/api/v1/attendance',
        attendance,
        {
          headers: {
            Accept: 'application/json',
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      if (response.data.success) {
        dispatch(fetchInternAttendanceRecords());
        toast.success(response.data.message);
      } else {
        toast.error(response.data.message);
      }
    } catch (error) {
      toast.error(error.response.data.message);
    }
  };



  return (
    <>
      <div className="flex justify-between items-center w-full py-10 px-10">
        <div className="text-3xl italic font-serif">Attendance Page</div>
        <div className="text-lg italic font-mono">Welcome user</div>
        <button
          onClick={handleLogout}
          className="w-[5%] bg-red-500 text-white rounded-2xl active:scale-75 active:transition-all active:ease-in"
        >
          Logout
        </button>
      </div>
      <div className="flex justify-center">
        <p className="pb-5 text-blue-500"> Date : {moment().format('LLLL')}</p>
        <button onClick={()=>{handleSubmit()}} className={`w-20 rounded-md text-white ml-10 h-10 bg-green-500 ${isPunchedIn ? 'bg-gray-500 cursor-not-allowed' : ''}`} disabled={isPunchedIn ? true : false}>Punch In</button>
        <button onClick={()=>{handleSubmit()}} className={`w-20 rounded-md text-white ml-10 h-10 bg-red-500 ${isPunchedOut ? 'bg-gray-500 cursor-not-allowed' : ''}`} disabled={isPunchedOut ? true : false}>Punch Out</button>
      </div>
      <Outlet />
    </>
  );
};

export default SystemLayout;
